        chunk = chunk.toString();
        self.push(chunk.replace(/(\r\n[Hh]ost: )\S+/, function(match, $1) {
            self.replaced = true;
            return $1 + self.host;
        }));
    }
    else {
        self.push(chunk);
    }

    cb();
};

module.exports = HeaderHostTransformer;
